mapper.select:
SELECT

FROM AC_ROLE_FUNC
WHERE ROLE_ID = #{ROLE_ID}
  AND FUNC_ID = #{FUNC_ID}

SELECT

FROM AC_ROLE_FUNC
WHERE 1=1

  AND ROLE_ID = #{ROLE_ID}


  AND APP_ID = #{APP_ID}

mapper.update:
UPDATE AC_ROLE_FUNC


     APP_ID = #{APP_ID},


     FUNC_GROUP_ID = #{FUNC_GROUP_ID},


 WHERE ROLE_ID = #{ROLE_ID}
   AND FUNC_ID = #{FUNC_ID}
