mapper.select:
SELECT

FROM OM_EMP_ORG
WHERE ORG_ID = #{ORG_ID}
  AND EMP_ID = #{EMP_ID}

mapper.delete:
DELETE FROM OM_EMP_ORG
WHERE ORG_ID = #{ORG_ID}
  AND EMP_ID = #{EMP_ID}

mapper.insert:
INSERT INTO OM_EMP_ORG (ORG_ID, EMP_ID, IS_MAIN
  )
VALUES (#{ORG_ID}, #{EMP_ID}, #{IS_MAIN}
  )

mapper.update:
UPDATE OM_EMP_ORG


     IS_MAIN = #{IS_MAIN},


 WHERE ORG_ID = #{ORG_ID}
   AND EMP_ID = #{EMP_ID}
