mapper.select:
SELECT

FROM OM_EMP_POSITION
WHERE POSITION_ID = #{POSITION_ID}
  AND EMP_ID = #{EMP_ID}

mapper.delete:
DELETE FROM OM_EMP_POSITION
WHERE POSITION_ID = #{POSITION_ID}
  AND EMP_ID = #{EMP_ID}

mapper.insert:
INSERT INTO OM_EMP_POSITION (POSITION_ID, EMP_ID, IS_MAIN
  )
VALUES (#{POSITION_ID}, #{EMP_ID}, #{IS_MAIN}
  )

mapper.update:
UPDATE OM_EMP_POSITION


     IS_MAIN = #{IS_MAIN},


 WHERE POSITION_ID = #{POSITION_ID}
   AND EMP_ID = #{EMP_ID}
