mapper.select:
		SELECT DIC_TYPE_ID,TYPE_NAME,TYPE_CODE,IS_USE
    	FROM SYS_DIC_TYPE
    	WHERE DIC_TYPE_ID = #{DIC_TYPE_ID}

	SELECT DIC_TYPE_ID,TYPE_NAME,TYPE_CODE
	FROM SYS_DIC_TYPE
	WHERE 1=1

  	AND  TYPE_CODE = #{TYPE_CODE}


  	AND  DIC_TYPE_ID = #{DIC_TYPE_ID}


  	AND  DIC_TYPE_ID != #{NOT_ID}

SELECT DIC_ID, TEXT, VALUE, SYS_DIC.IS_USE, PID, TYPE_ID, SYS_DIC_TYPE.TYPE_CODE, SYS_DIC_TYPE.TYPE_NAME, SYS_DIC_TYPE.IS_USE TYPE_IS_USE FROM SYS_DIC INNER JOIN SYS_DIC_TYPE ON SYS_DIC.TYPE_ID = SYS_DIC_TYPE.DIC_TYPE_ID WHERE 1 = 1
AND TYPE_ID = #{t.DIC_TYPE_ID}
AND SYS_DIC_TYPE.TYPE_CODE = #{t.TYPE_CODE}

SELECT DIC_ID, TEXT, VALUE, SYS_DIC.IS_USE, PID, TYPE_ID, SYS_DIC_TYPE.TYPE_CODE, SYS_DIC_TYPE.TYPE_NAME, SYS_DIC_TYPE.IS_USE TYPE_IS_USE FROM SYS_DIC INNER JOIN SYS_DIC_TYPE ON SYS_DIC.TYPE_ID = SYS_DIC_TYPE.DIC_TYPE_ID WHERE 1 = 1
AND TYPE_ID = #{TYPE_ID}
AND VALUE = #{VALUE}
AND DIC_ID != #{NOT_ID}
