mapper.select:
SELECT

FROM AC_APPLICATION
WHERE 1=1

  AND  APP_CODE = #{APP_CODE}


  AND  APP_ID != #{NOT_ID}

mapper.delete:
DELETE AC_APPLICATION,AC_FUNC_GROUP,AC_FUNCTION,AC_MENU,AC_ROLE_FUNC,AC_OPER_FUNC
FROM AC_APPLICATION LEFT JOIN AC_FUNC_GROUP ON AC_APPLICATION.APP_ID = AC_FUNC_GROUP.APP_ID LEFT JOIN AC_FUNCTION ON AC_FUNCTION.FUNC_GROUP_ID = AC_FUNC_GROUP.FUNC_GROUP_ID LEFT JOIN AC_MENU ON AC_MENU.APP_ID = AC_APPLICATION.APP_ID LEFT JOIN AC_ROLE_FUNC ON AC_ROLE_FUNC.APP_ID = AC_APPLICATION.APP_ID LEFT JOIN AC_OPER_FUNC ON AC_OPER_FUNC.APP_ID = AC_APPLICATION.APP_ID
WHERE AC_APPLICATION.APP_ID =#{APP_ID}
